body {
    margin: 0;
    font-family: Arial, sans-serif;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    overflow: hidden;
}

.app-bar {
    width: 100%;
    height: 75px;
    background-color: #333;
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
}

.camera-canvas {
    position: absolute;
    transform: scale(0.5, 0.5);
    transform-origin: 0 0;
    border-radius: 50%;
    left: 10px;
    top: 10px;
}

.id-section {
    margin: 0 10px;
}

.id-section label {
    margin-right: 5px;
}

.controls {
    margin: 10px;
    display: flex;
    justify-content: center;
}

.controls button {
    margin: 0 10px;
    padding: 10px 20px;
    font-size: 16px;
}

.canvas-container {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 950px;
    height: 475px;
    position: relative;
    border: 2px solid black;
    margin-bottom: 20px;
}

#main-canvas {
    width: 100%;
    height: 100%;
}

.loading {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    display: flex;
    flex-direction: column;
    align-items: center;
}

.spinner-text {
    margin-bottom: 10px;
}

.sk-spinner-pulse {
    width: 40px;
    height: 40px;
    background-color: #333;
    border-radius: 50%;
    animation: sk-pulse 1.2s infinite ease-in-out;
}

@keyframes sk-pulse {
    0%, 100% {
        transform: scale(0);
    }
    50% {
        transform: scale(1);
    }
}
